

page = function(res)
{
	this.ctype = 'text/html';
	this.res = res;

	this.start = function()
	{
		this.res.writeHead(200, {'Content-Type': this.ctype});

		this.res.write("<html>\n<head>\n<title>gasview</title>\n");
		this.res.write("<link rel='stylesheet' href='css/style.css' />\n</head>\n<body>\n");
		this.res.write("<div id=\"gv_cont\">\n<div id='gv_head'><h1>gasview</h1></div>\n<div id=\"gv_menu\">menu item one</div>\n<div id=\"gv_body\">\n");
	}

	this.write = function(data)
	{
		this.res.write(data);
	}

	this.end = function()
	{
		res.write("\n</div>\n</div>\n</body>\n</html>\n");
		this.res.end();
	}
}

module.exports.page = page;
